def line_cpu() -> Line:
    c = (
        Line(init_opts=opts.InitOpts(width="1960px", height="800px"))
        .set_global_opts(
            tooltip_opts = opts.TooltipOpts(is_show=True),
            xaxis_opts = opts.AxisOpts(
                type_ = "value",
                max_ = "dataMax"
            ),
            yaxis_opts = opts.AxisOpts(
                type_ = "value",
                axistick_opts = opts.AxisTickOpts(is_show=True),
                splitline_opts = opts.SplitLineOpts(is_show=True),
            ),
            datazoom_opts=[
                opts.DataZoomOpts(xaxis_index=0),
                opts.DataZoomOpts(type_="inside", yaxis_index=0),
            ],
        )
        .add_xaxis(xaxis_data = [item[1] for item in hardware_cpu])
        .add_yaxis(
            series_name = "CPU测试数据",
            y_axis = [item[0] for item in hardware_cpu],
            is_symbol_show = True,
            label_opts = opts.LabelOpts(is_show=False),
        )
    )
    return c

